import { View, Text } from 'react-native';
import React from 'react';

export default function OneItemBanBe({ item }) {
  return (
      

    <Box p='3'>
        <HStack style={{alignItems:'center'}} space='2'>
            {
              item.isSelected
              ?
              <Checkbox rounded='2xl' accessibilityLabel="This is a dummy checkbox" defaultIsChecked onChange={() => updateListOfYourFriend(item.id, item.isSelected)} />
              :
              <Checkbox rounded='2xl' accessibilityLabel="This is a dummy checkbox" onChange={() => updateListOfYourFriend(item.id)} />
            }
            <Image source={{ uri: item.photoURL }} alt="photoURL" size="xs" borderRadius={100} />
            <Text>{item.fullName}</Text>
        </HStack>
    </Box>


  );
}
