import { View, Text } from 'react-native';
import { collection, addDoc, Timestamp, doc, getDoc } from "firebase/firestore";
import { database } from '../../firebase';
import React, { useState } from 'react';

const GetObjectUserById = (id) => {
    const [objectUser, setObjectUser] = useState(null);
    const UsersDocRef = doc(database, "Users", id);
    getDoc(UsersDocRef)
    .then(docSnap => {
        setObjectUser(docSnap.data());
    })
    .catch(error => {
        console.log('(x) error in serivice GetObjectUserById: ', error);
    });
    return objectUser;
};
export default GetObjectUserById;
