import { View, Text, Button, TextInput } from 'react-native'
import React, { useContext, useEffect, useState } from 'react'
import { AuthContext } from '../provider/AuthProvider';
import { collection, getDocs, orderBy, query, onSnapshot } from 'firebase/firestore';
import { database } from '../../firebase';

export default function ChatScreen({ route, navigation }) {

    const { currentUser:{
        displayName,
        email,
        photoURL
    }, socket } = React.useContext(AuthContext);
    const [currentMessage, setCurrentMessage] = useState('');
    const { id } = route.params;
    const { name } = route.params;
    const [messageList, setMessageList] = useState([]);

    //Get first messages[] from firestore
    const getRoomMessagesFromFirestore = async () => {
      const DATALIST_MESSAGES = [];
      const querySnapshot = await getDocs(collection(database, "RoomMessages"));
      querySnapshot.forEach((doc) => {
        setMessageList((list) => [...list, doc.data()]);
      });
    }
    useEffect(() => {
      getRoomMessagesFromFirestore();
    }, []);

    console.log(messageList);

    //Send message
    const sendMessage = () => {
        if(currentMessage != "") {
            const objectMessage = {
                room: name,
                author: email,
                message: currentMessage,
                time: new Date(Date.now()).getTime()
            }
            socket.emit("send_message", objectMessage);
            setMessageList((list) => [...list, objectMessage]);
        }
    }

    //Listening message
    useEffect(() => {
        socket.on("receive_message", (data) => {
            setMessageList((list) => [...list, data]);
        });
    }, [socket]);
    

  return (
    <View style={{flex:1, alignItems:'center'}}>
      <View>
        <Text>Room: {name}</Text>
      </View>
      <View style={{flex:1,backgroundColor:'lightgrey', width:'100%'}}>
        {messageList.map((msgObject) => {
            return <Text key={email === msgObject.email ? Math.random() : Math.random()}>{msgObject.message}</Text>;
        })}
      </View>
      <View style={{flexDirection:'row'}}>
        <TextInput placeholder='Enter message...' onChangeText={(e) => setCurrentMessage(e)} value={currentMessage} />
        <Button title='Send message' onPress={sendMessage}/>
      </View>
    </View>
  )
}
