import { View, Text, StyleSheet } from 'react-native';
import React, { useCallback, useEffect, useState, useMemo, useContext } from 'react';
import { Box, Center, Checkbox, HStack, Icon, Image, Input, NativeBaseProvider, ScrollView, Pressable, FlatList, Modal, Button } from 'native-base';
import { Ionicons, MaterialCommunityIcons, MaterialIcons } from '@expo/vector-icons';
import { AuthContext } from '../provider/AuthProvider';
import { collection, addDoc } from 'firebase/firestore';
import { database } from '../../firebase';
import Toast from 'react-native-toast-message';

export default function CreateRoomScreen() {
  //0. Khởi tạo biến
  const { currentUser } = useContext(AuthContext);
  const [modalVisible, setModalVisible] = useState(false);
  const [listOfYourFriend, setListOfYourFriend] = useState(null);
  const [isNullListFriend, setIsNullListFriend] = useState(true);
  const [listCheckboxSelected,setListCheckboxSelected] = useState(0);
  const [isShowDanhBa, setIsShowDanhBa] = useState(false);
  const DATA = [{id:'1', fullName: 'Rowan Africa 1', photoURL:'https://wallpaperaccess.com/full/317501.jpg', isSelected: false}, {id:'2', fullName: 'Rowan Africa 2', photoURL:'https://wallpaperaccess.com/full/317501.jpg', isSelected: false}, {id:'3', fullName: 'Rowan Africa 3', photoURL:'https://wallpaperaccess.com/full/317501.jpg', isSelected: false}, {id:'4', fullName: 'Rowan Africa 4', photoURL:'https://wallpaperaccess.com/full/317501.jpg', isSelected: false}, {id:'5', fullName: 'Rowan Africa 5', photoURL:'https://wallpaperaccess.com/full/317501.jpg', isSelected: false}, {id:'6', fullName: 'Rowan Africa 6', photoURL:'https://wallpaperaccess.com/full/317501.jpg', isSelected: false}, {id:'7', fullName: 'Rowan Africa 7', photoURL:'https://wallpaperaccess.com/full/317501.jpg', isSelected: false}, {id:'8', fullName: 'Rowan Africa 8', photoURL:'https://wallpaperaccess.com/full/317501.jpg', isSelected: false}, {id:'9', fullName: 'Rowan Africa 9', photoURL:'https://wallpaperaccess.com/full/317501.jpg', isSelected: false}];
  useEffect(() => {
      setListOfYourFriend(DATA);
      setIsNullListFriend(false);
      console.log('Effect >> Loaded data from firestore');
  },[]);

  console.log('App Rendered');

  useEffect(() => {
        if(listOfYourFriend == null){
          return;
        }
        console.log('Effect >> listOfYourFriend has been changed');
        let count = 0;
        for(let i = 0; i<listOfYourFriend.length ; i++){
          if(listOfYourFriend[i].isSelected){
            count++;
            console.log(count);
          }
        }
        if(count > 0){
          setListCheckboxSelected(count);
          setModalVisible(true);
        }
        if(count <=0 ){
          setModalVisible(false);
        }
  },[listOfYourFriend]);

  //1. Tạo hàm cần thiết
  const handleCreateRoom = () => {
    console.log('List checkbox selected', listCheckboxSelected);
      Toast.show({
        type: 'error',
        text1: 'Chưa đủ thông tin',
        text2: 'Vui lòng nhập tên nhóm'
      });
  };
  const updateListOfYourFriend = useCallback((idUser, isChecked) => {
      setListOfYourFriend(
        (prevList) => prevList.map (
          (userFriend) => userFriend.id === idUser ? {...userFriend, isSelected: !isChecked} : userFriend
        )
      );
  }, []);
  const renderItem = ({ item }) => (
    <OneItem item={item} />
  );
  const OneItem = ({ item }) => (
    <Box p='3'>
        <HStack style={{alignItems:'center'}} space='2'>
            {
              item.isSelected
              ?
              <Checkbox rounded='2xl' accessibilityLabel="This is a dummy checkbox" defaultIsChecked onChange={() => updateListOfYourFriend(item.id, item.isSelected)} />
              :
              <Checkbox rounded='2xl' accessibilityLabel="This is a dummy checkbox" onChange={() => updateListOfYourFriend(item.id)} />
            }
            <Image source={{ uri: item.photoURL }} alt="photoURL" size="xs" borderRadius={100} />
            <Text>{item.fullName}</Text>
        </HStack>
    </Box>
  );

  







  //1. Render html
  return (
    <NativeBaseProvider style={{overflow: 'hidden'}}>
      {/* div 0 */}
      <Toast position='bottom' bottomOffset={20} />
      {/* div 1 */}
      <Box mt='0.5'>
        <HStack borderWidth="0.4" borderColor="grey" borderRadius="2xl">
            <Box borderRadius="lg" p='5'>
              <MaterialIcons name="camera-enhance" size={32} color="#1e13f0" />
            </Box>
            <Box style={{flex:1, alignItems:'center',justifyContent:'center'}}>
                <Input 
                    variant='unstyled'
                    placeholder='Đặt tên nhóm mới'
                    size='lg'
                />
            </Box>
        </HStack>
      </Box>
      {/* div 2 */}
      <Box>
        <Input
            InputLeftElement={
                <Icon as={<Ionicons name="search-circle-outline" color="black" />} size='lg' color='black' />
            }
            placeholder='Tìm tên hoặc số điện thoại'
        />
      </Box>
      {/* div 3 */}
      <Box borderWidth="0.4" borderColor="grey" borderRadius="2xl" p='5'>
        <HStack>
            <Pressable style={ isShowDanhBa ? css.fadeEffect : css.boldEffect } onPress={() => setIsShowDanhBa(!isShowDanhBa)}>
                <Center _text={{ color: isShowDanhBa ? "coolGray.500" : "coolGray.800" }}>
                BẠN BÈ
                </Center>
            </Pressable>
            <Pressable style={ isShowDanhBa ? css.boldEffect : css.fadeEffect } onPress={() => setIsShowDanhBa(!isShowDanhBa)}>
                <Center _text={{ color: isShowDanhBa ? "coolGray.800" : "coolGray.500" }}>
                DANH BẠ
                </Center>
            </Pressable>
        </HStack>
      </Box>
      {/* div 4 */}
        <FlatList
            data={listOfYourFriend}
            renderItem={renderItem}
            keyExtractor={(item) => item.id}
        />
      {/* div 5 */}
      <Box style={modalVisible ? css.showMyModal : css.hideMyModal}>
        <Text style={{textAlign:'center'}}>Đã chọn: {listCheckboxSelected}</Text>
        <HStack space={3} style={{flexWrap:'wrap'}}>
          <Image source={{uri: 'https://wallpaperaccess.com/full/317501.jpg'}} alt="photoURL" size="xs" borderRadius={100} />
          {
            !isNullListFriend
            &&
            listOfYourFriend.map((obj) => {
              if(obj.isSelected)
                return <Image key={obj.id} source={{uri: obj.photoURL}} alt="photoURL" size="xs" borderRadius={100} />;
            })
          }
          <View style={css.btnCreate}>
            <Button variant='subtle' size='lg' borderRadius={150} onPress={handleCreateRoom}>Tạo</Button>
          </View>
        </HStack>
      </Box>
    </NativeBaseProvider>
  );
}

const css = StyleSheet.create({
    fadeEffect: {
        borderBottomWidth: 1,
        borderBottomColor: 'lightgrey',
        width: '50%'
    },
    boldEffect: {
        borderBottomWidth: 1,
        borderBottomColor: 'black',
        width: '50%'
    },
    showMyModal: {
      position:'absolute',
      width: '100%',
      bottom: 0,
      backgroundColor:'#ced3db',
      padding: 8
    },
    hideMyModal: {
      display: 'none'
    },
    btnCreate: {
      position: 'absolute',
      right:0,
      top:0
    }
});
