import { StyleSheet, Text, View, TextInput } from 'react-native'
import React from 'react'

const App = () => {
  return (
    <View style={styles.container}>
      
        <View style={styles.centerDiv}>
            {/* View Header */}
            <View style={styles.headerDiv}>
                <Text style={styles.textLogo}>Zalo Fake</Text>
                <Text>Đăng nhập tài khoản UChat</Text>
                <Text>để kết nối ứng dụng UChat</Text>
            </View>
            {/* View Tổng Auth */}
            <View style={styles.authDiv}>
                <View
                    style={{
                        flexDirection: 'row',
                        backgroundColor: 'pink',
                        padding: 10
                    }}>
                    <View
                        style={{
                            borderColor:'black',borderBottomWidth:1,borderRightWidth:1, width: '50%', textAlign: 'center', fontWeight: 'bold'
                        }}>
                        ĐĂNG NHẬP
                    </View>
                    <View
                        style={{
                            borderColor:'grey',borderBottomWidth:1, width: '50%', textAlign: 'center'
                        }}>
                        ĐĂNG KÝ
                    </View>
                </View>
                <View
                    style={{padding: 15}}
                >
                    <TextInput placeholder='Địa chỉ email'
                        style={{
                            borderColor: 'black', borderBottomWidth: 1, outLine: 'none'
                        }}
                    />
                    <TextInput placeholder='Mật khẩu'
                        style={{
                            borderColor: 'black', borderBottomWidth: 1, outLine: 'none'
                        }}
                    />
                </View>
            </View>
            <View></View>
        </View>

    </View>
  )
}

export default App

const styles = StyleSheet.create({
    authDiv: {
        backgroundColor: 'white',
        height: 100
    },
    headerDiv: {
        textAlign: 'center',
    },
    container: {
        justifyContent: 'center',
        alignItems: 'center',
        flex: 1,
        // backgroundImage: 'linear-gradient(to top, #cfd9df 0%, #e2ebf0 100%)',
        backgroundImage: 'linear-gradient(120deg, #89f7fe 0%, #66a6ff 100%)',
    },
    centerDiv: {
        position: 'absolute',
        top: '45%',
        left: '50%',
        transform: 'translate(-50%, -50%)',
    },
    textLogo: {
        fontSize: 50,
        color: 'blue',
        fontWeight: '900'
    },
});
