import { View, Text, StyleSheet } from 'react-native';
import React, { useCallback, useEffect, useState } from 'react';
import { Box, Center, Checkbox, HStack, Icon, Image, Input, NativeBaseProvider, ScrollView, Pressable, FlatList } from 'native-base';
import { Ionicons, MaterialCommunityIcons } from '@expo/vector-icons';

export default function CreateRoomScreen() {
  //0. Khởi tạo biến
  const [listOfYourFriend, setListOfYourFriend] = useState(null);
  const [isShowDanhBa, setIsShowDanhBa] = useState(false);
  const DATA = [{id:'1', fullName: 'Rowan Africa 1', photoURL:'https://wallpaperaccess.com/full/317501.jpg', isSelected: false}, {id:'2', fullName: 'Rowan Africa 2', photoURL:'https://wallpaperaccess.com/full/317501.jpg', isSelected: false}, {id:'3', fullName: 'Rowan Africa 3', photoURL:'https://wallpaperaccess.com/full/317501.jpg', isSelected: false}, {id:'4', fullName: 'Rowan Africa 4', photoURL:'https://wallpaperaccess.com/full/317501.jpg', isSelected: false}, {id:'5', fullName: 'Rowan Africa 5', photoURL:'https://wallpaperaccess.com/full/317501.jpg', isSelected: false}, {id:'6', fullName: 'Rowan Africa 6', photoURL:'https://wallpaperaccess.com/full/317501.jpg', isSelected: false}, {id:'7', fullName: 'Rowan Africa 7', photoURL:'https://wallpaperaccess.com/full/317501.jpg', isSelected: true}, {id:'8', fullName: 'Rowan Africa 8', photoURL:'https://wallpaperaccess.com/full/317501.jpg', isSelected: false}, {id:'9', fullName: 'Rowan Africa 9', photoURL:'https://wallpaperaccess.com/full/317501.jpg', isSelected: false}];

  // setListOfYourFriend(DATA);
  // console.log(listOfYourFriend);
  useEffect(() => {
    setTimeout(() => {
      setListOfYourFriend(DATA);
    }, 500);
  },[]);

  console.log('Rendered', listOfYourFriend);

  // for(let i = 0 ; i<DATA.length ; i++){
  //   setListOfYourFriend([...listOfYourFriend,DATA[i]]);
  //   console.log(DATA[i]);
  // }

  // const setDataFromFirestoreToUseStateList = useCallback((DATAFROMFIRESTORE) => {
  //   setListOfYourFriend(DATAFROMFIRESTORE.map(
  //     (dataObject) => dataObject
  //   ));
  //   // setListOfYourFriend([...listOfYourFriend,{id:1,name:'aaa',age:21}]);
  //   console.log('Do one time', listOfYourFriend);
  // },[listOfYourFriend]);

  // const addOneObjectToListOfYourFriend = useCallback((userFriend) => {
  //   setListOfYourFriend([...listOfYourFriend, userFriend]);
  //   console.log('do one time and listOfYourFriend: ', listOfYourFriend);
  // },[]);

  // DATA.map((userFriend) => {
    
  //   addOneObjectToListOfYourFriend(userFriend);

  // });
  // console.log('Rendered');

  const updateListOfYourFriend = useCallback((idUser) => {
      setListOfYourFriend(
        (prevList) => prevList.map (
          (userFriend) => userFriend.id === idUser ? {...userFriend, isSelected: true} : userFriend
        )
      );
  }, [listOfYourFriend]);

  //1. Tạo hàm cần thiết
  const renderItem = ({ item }) => (
    <OneItem item={item} />
  );
  const OneItem = ({ item }) => (
    <Box p='3'>
        <HStack style={{alignItems:'center'}} space='2'>
            {
              item.isSelected
              ?
              <Checkbox rounded='2xl' accessibilityLabel="This is a dummy checkbox" defaultIsChecked onChange={() => updateListOfYourFriend(item.id)} />
              :
              <Checkbox rounded='2xl' accessibilityLabel="This is a dummy checkbox" onChange={() => updateListOfYourFriend(item.id)} />
            }
            <Image source={{ uri: item.photoURL }} alt="photoURL" size="xs" borderRadius={100} />
            <Text>{item.fullName}</Text>
        </HStack>
    </Box>
  );

  







  //1. Render html
  return (
    <NativeBaseProvider>
      <Text>CreateRoomScreen</Text>
      {/* div 1 */}
      <Box>
        <HStack borderWidth="0.4" borderColor="grey" borderRadius="2xl">
            <Box borderRadius="lg">
                <MaterialCommunityIcons name="camera-control" size={44} color="black" />
            </Box>
            <Box style={{flex:1}}>
                <Input 
                    variant='unstyled'
                    placeholder='Đặt tên nhóm mới'
                    size='lg'
                />
            </Box>
        </HStack>
      </Box>
      {/* div 2 */}
      <Box>
        <Input
            InputLeftElement={
                <Icon as={<Ionicons name="search-circle-outline" color="black" />} size='lg' color='black' />
            }
            placeholder='Tìm tên hoặc số điện thoại'
        />
      </Box>
      {/* div 3 */}
      <Box borderWidth="0.4" borderColor="grey" borderRadius="2xl" p='5'>
        <HStack>
            <Pressable style={ isShowDanhBa ? css.fadeEffect : css.boldEffect } onPress={() => setIsShowDanhBa(!isShowDanhBa)}>
                <Center _text={{ color: isShowDanhBa ? "coolGray.500" : "coolGray.800" }}>
                BẠN BÈ
                </Center>
            </Pressable>
            <Pressable style={ isShowDanhBa ? css.boldEffect : css.fadeEffect } onPress={() => setIsShowDanhBa(!isShowDanhBa)}>
                <Center _text={{ color: isShowDanhBa ? "coolGray.800" : "coolGray.500" }}>
                DANH BẠ
                </Center>
            </Pressable>
        </HStack>
      </Box>
      {/* div 4 */}
        <FlatList
            data={DATA}
            renderItem={renderItem}
            keyExtractor={(item) => item.id}
        />
    </NativeBaseProvider>
  );
}

const css = StyleSheet.create({
    fadeEffect: {
        borderBottomWidth: 1,
        borderBottomColor: 'lightgrey',
        width: '50%'
    },
    boldEffect: {
        borderBottomWidth: 1,
        borderBottomColor: 'black',
        width: '50%'
    }
});
