import { StyleSheet, Text, View, TextInput, Button, Pressable, SafeAreaView } from 'react-native';
import React, { useEffect, useState } from 'react';
import IconFontAwesome5 from 'react-native-vector-icons/FontAwesome5';

export default function AuthScreen({ navigation }){
    const [isRegisterTabShow, setIsRegisterTabShow] = useState(false);
    const changeToRegisterTab = () => {
        setIsRegisterTabShow(false);
    };
    const changeToLoginTab = () =>{
        setIsRegisterTabShow(true);
    };
    return(
    <View>
        {/* View Header */}
        <View>
            <Text>Zalo Fake</Text>
            <Text>Đăng nhập tài khoản UChat</Text>
            <Text>để kết nối ứng dụng UChat</Text>
            <Text>&emsp;</Text>
        </View>
        
        


        {/* Login component */}
        <View style={{backgroundColor: 'white'}}>
            <Text onPress={props.brand}>AAAAAA</Text>
            <View style={{flexDirection: 'row',padding: 10}}>
                <Text
                    style={{borderColor:'black',borderBottomWidth:1,borderRightWidth:1, width: '50%', textAlign: 'center', fontWeight: 'bold'}}>
                    ĐĂNG NHẬP
                </Text>
                <Pressable style={{width: '50%'}}>
                    <Text style={{borderColor:'grey',borderBottomWidth:1, textAlign: 'center'}}>
                        ĐĂNG KÝ
                    </Text>
                </Pressable>
            </View>
            <View style={{padding: 30, textAlign: 'center'}}>
                <TextInput placeholder='Địa chỉ email' style={{borderColor:'black',borderBottomWidth:1,outline:'none'}}/>
                <TextInput placeholder='Mật khẩu' style={{borderColor:'black',borderBottomWidth:1,outline:'none'}}/>
                <Text>&emsp;</Text>
                <Button style={{marginTop: 20}} title="Đăng nhập với mật khẩu"/>
                <Text>&emsp;</Text>
                <Text>Quên mật khẩu?</Text>
            </View>
        </View>












        <View>
            <Text>&emsp;</Text>
            <Text>Tiếng Việt</Text>
            <Text>Copyright <IconFontAwesome5 name='copyright' color="black" solid/> 2022 Nhóm 9 UltimtateChat Application</Text>
            <Text>Terms of Use | Privacy Policy</Text>
        </View>
    </View>
    )
};
