import { StyleSheet, Text, View } from 'react-native'
import React from 'react'

const App = () => {
  return (
    <View style={styles.container}>
      
        <View style={styles.centerDiv}>
            <View style={styles.headerDiv}>
                <Text style={styles.textLogo}>Zalo Fake</Text>
                <Text>Đăng nhập tài khoản UChat</Text>
                <Text>để kết nối ứng dụng UChat</Text>
            </View>
            <View style={styles.authDiv}>
                <View style={{flexDirection: 'row'}}>
                    <View style={styles.buttonStyle}>ĐĂNG NHẬP</View>
                    <View>ĐĂNG KÝ</View>
                </View>
            </View>
            <View></View>
        </View>

    </View>
  )
}

export default App

const styles = StyleSheet.create({
    buttonStyle: {
        borderTopRightRadius: 10,
        borderTopLeftRadius: 10,
        borderBottomRightRadius: 10,
        borderBottomLeftRadius: 10
    }, 
    authDiv: {
        backgroundColor: 'white',
        padding: '30px'
    },
    headerDiv: {
        textAlign: 'center',
    },
    container: {
        justifyContent: 'center',
        alignItems: 'center',
        flex: 1,
        // backgroundImage: 'linear-gradient(to top, #cfd9df 0%, #e2ebf0 100%)',
        backgroundImage: 'linear-gradient(120deg, #89f7fe 0%, #66a6ff 100%)',
    },
    centerDiv: {
        position: 'absolute',
        top: '45%',
        left: '50%',
        transform: 'translate(-50%, -50%)',
    },
    textLogo: {
        fontSize: 50,
        color: 'blue',
        fontWeight: '900'
    },
});
